import React, {  useState } from "react";
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from "reactstrap";

const EditTodo = ({ todo }) => {
  const [Description, setDescription] = useState(todo.description);
  const [modalOpen, setModalOpen] = useState(false);

  const toggleModal = () => {
    setModalOpen(!modalOpen);
  };
  // edit function
 const updateDescription = async () => {
    try { 
      
      const body = { Description };
      const response = await fetch(
        `http://localhost:5000/todos/${todo.id}`,
        {
          method: "PUT",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(body),
        }
      );
      window.location = "/ListeTodos";
      toggleModal();
    } catch (err) {
      console.error(err.message);
    }
  
  };
  
  return (
    <React.Fragment>
      <Button  style={{position:"relative", left:"75%"}} color="warning" onClick={toggleModal}>
        Edit
      </Button>

      <Modal isOpen={modalOpen}
       
        modalTransition={{ timeout: 700 }}
        backdropTransition={{ timeout: 1300 }}
        className={className}
       toggle={toggleModal}>
        <ModalHeader toggle={toggleModal}>Edit Todo</ModalHeader>
        <ModalBody>
          <textarea
            className="form-control"
            value={Description}
            onChange={(e) => setDescription(todo.Description)}
          />
        </ModalBody>
        <ModalFooter>
          <Button color="success" onClick={updateDescription}>
            Save
          </Button>{" "}
          <Button color="danger" onClick={toggleModal}>
            Cancel
          </Button>
        </ModalFooter>
      </Modal>
    </React.Fragment>
  );
};
 {/* return (
    <React.Fragment>
      
      <button
        type="button"
        className="btn btn-warning"
        data-toggle="modal"
        data-target={"#id${todo.todo_id}"} 
        style={{position:"relative", left:"75%"}}
        onClick={updateDescription}
        >
        Edit
      </button>
    {/*  <div class="modal" id="myModal">
        <div class="modal-dialog">
          <div class="modal-content">
            <div class="modal-header">
              <h4 class="modal-title">Edit Todo</h4>
              <button type="button" class="close" data-dismiss="modal"></button>
            </div>
            <div className="modal-body">modal body </div>
            <div className="modal-footer">
              <button type="button" className="btn btn-danger" data-dismiss="modal">
                &times;
              </button>
            </div>
            <div class="modal-body">
              <input type="text" className="form-control"/>
            </div>
            <div class="modal-footer">
              <button type="button" class="btn btn-warning " data-dismiss="mdal"> Edit</button>
              <button type="button" class="btn btn-warning " data-dismiss="mdal">Close</button>
            </div>

          </div>

        </div>
  </div>
    

            
           
    </React.Fragment>
  );
};*/}
export default EditTodo;
